<?php\n$dbHost = "host"; // e.g., localhost or provided by hosting provider\n$username = "user"; // Your MySQL username (including prefix if applicable)\n$password = "password";\n$databasePrefix = substr($username, 0, strpos($username, '_') + 1); // Finds the prefix up to and including first underscore\n\n// Connect to MySQL\n$mysqli = new mysqli($dbHost, $username, $password);\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$dbName = $databasePrefix . 'dbname'; // Create database name with prefix\n$dbUser = $databasePrefix . 'dbuser'; // Create user name with prefix\n\n// Create Database\n$queryCreateDb = "CREATE DATABASE IF NOT EXISTS {$dbName}";\nif ($mysqli->query($queryCreateDb) === TRUE) {\n    echo "Database created successfully\n";\n} else {\n    echo "Error creating database: " . $mysqli->error;\n}\n\n// Select Database\n$mysqli->select_db($dbName);\n\n// Create User and Grant Privileges\n$queryCreateUser = "CREATE USER '{$dbUser}'@'%' IDENTIFIED BY 'somepass'";\nif ($mysqli->query($queryCreateUser) === TRUE) {\n    echo "User created successfully\n";\n} else {\n    echo "Error creating user: " . $mysqli->error;\n}\n\n$queryGrantPrivileges = "GRANT SELECT, INSERT, UPDATE, DELETE ON {$dbName}.* TO '{$dbUser}'@'%'";\nif ($mysqli->query($queryGrantPrivileges) === TRUE) {\n    echo "Privileges granted successfully\n";\n} else {\n    echo "Error granting privileges: " . $mysqli->error;\n}\n\n// Import SQL File\n$filePath = 'path/to/sqlfile.sql';\n$sqlContent = file_get_contents($filePath);\n\nif ($sqlContent !== false) {\n    if ($mysqli->multi_query($sqlContent)) {\n        do {\n            /* store first result set */\n            if ($result = $mysqli->use_result()) {\n                while ($row = $result->fetch_array(MYSQLI_NUM)) {\n                    printf("%s\n", $row[0]);\n                }\n                $result->free();\n            }\n            /* print divider */\n            if ($mysqli->more_results()) {\n                echo "-----------------\n";\n            }\n        } while ($mysqli->next_result());\n    } else {\n        echo "Error importing SQL file: " . $mysqli->error;\n    }\n} else {\n    echo "Failed to open SQL file\n";\n}\n\n$mysqli->close();\n?>