# Define basic permissions for unauthenticated users ('*') and regular users ('user')\n$wgGroupPermissions['*']['edit'] = false;\n$wgGroupPermissions['user']['edit'] = true;\n\n# Define 'Human Resource' group with edit permission\n$wgGroupPermissions['Human Resource']['edit'] = true;\n\n# Optionally, you can grant additional permissions if needed\n# $wgGroupPermissions['Human Resource']['protect'] = true; // Uncomment and set other as necessary\n\n# Disable automatic account creation for unauthenticated users\n$wgGroupPermissions['*']['createaccount'] = false;\n\n# Define pages that are accessible without login (e.g., main page or login page)\n$wgWhitelistRead = array( "Main Page", "Special:UserLogin", "-", "MediaWiki:Monobook.css" );\n\n# Deny read access to unauthenticated users\n$wgGroupPermissions['*']['read'] = false;