<?php\n\nclass Person {\n    public $id;\n    public $name;\n    // other properties...\n\n    public function __construct($data) {\n        $this->id = $data['id'];\n        $this->name = $data['name'];\n        // assign other properties...\n    }\n}\n\n// Assuming $pdo is your PDO database connection\nfunction fetchAllPersons($pdo) {\n    try {\n        $stmt = $pdo->query('SELECT * FROM users'); // Use a prepared statement if you need dynamic SQL\n        $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n        \n        $persons = array();\n        foreach ($results as $row) {\n            $persons[] = new Person($row);\n        }\n        \n        return $persons;\n    } catch (PDOException $e) {\n        // handle exception, log error...\n    }\n}\n\n// Usage\n$pdo = new PDO('mysql:host=localhost;dbname=test', 'user', 'password');\n$allPersons = fetchAllPersons($pdo);\n\nforeach ($allPersons as $person) {\n    echo "Person ID: {$person->id}, Name: {$person->name}<br>";\n}