class YourModel extends Zend_Db_Table_Abstract {\n    protected $_name = 'your_table_name';\n\n    public function insert(array $data) {\n        // Iterate through each field and set it to NULL if it is blank\n        foreach ($data as &$value) {\n            if ($value === '') {\n                $value = null;\n            }\n        }\n        unset($value); // Break the reference within the loop\n\n        return parent::insert($data);\n    }\n}