<?php\n$to = 'recipient@example.com';\n$subject = 'Email with Inline Image';\n\n// Create a boundary string\n$boundary = md5("random"); \n\n$message = "--{$boundary}\r\n";\n$message .= "Content-Type: text/html; charset=\"ISO-8859-1\"\r\n\r\n";\n\n$message .= "<html><body>";\n$message .= '<img src="cid:image_id" alt="Test Image">';\n$message .= "</body></html>";\n\n// Read the image content\n$imageData = chunk_split(base64_encode(file_get_contents('path/to/image.png')));\n\n$message .= "--{$boundary}\r\n";\n$message .= "Content-Type: image/png; name=\"image.png\"\r\n";\n$message .= "Content-Transfer-Encoding: base64\r\n";\n$message .= "Content-ID: <image_id>\r\n\r\n"; // Define the cid\n$message .= $imageData;\n$message .= "--{$boundary}--";\n\n$headers  = 'MIME-Version: 1.0' . "\r\n";\n$headers .= 'Content-type: multipart/mixed; boundary="' . $boundary . '"' . "\r\n";\n\nmail($to, $subject, $message, $headers);\n?>