<?php\n\nfunction extractImagesFromPDF($pdfPath, $outputPath) {\n    // Check if the pdfimages command exists\n    if (!system("which pdfimages", $returnCode)) {\n        throw new Exception("The 'pdfimages' command is not available. Please install poppler-utils.");\n    }\n\n    // Use pdfimages to extract images from PDF\n    $command = "pdfimages -png '{$pdfPath}' '{$outputPath}/img-'";\n    exec($command, $output, $returnVar);\n\n    if ($returnVar !== 0) {\n        throw new Exception("Error extracting images: " . implode("\n", $output));\n    }\n\n    echo "Images extracted successfully to '{$outputPath}'.";\n}\n\ntry {\n    extractImagesFromPDF('/path/to/your/file.pdf', '/path/to/output/directory');\n} catch (Exception $e) {\n    echo 'Caught exception: ',  $e->getMessage(), "\n";\n}