try {\n    $pdo = new PDO('your_connection_string');\n} catch (PDOException $e) {\n    echo "Connection error: " . $e->getMessage();\n}\n\ntry {\n    $stmt = $pdo->prepare("SELECT * FROM your_table");\n} catch (PDOException $e) {\n    echo "Preparation error: " . $e->getMessage();\n}\n\ntry {\n    $stmt->execute();\n} catch (PDOException $e) {\n    echo "Execution error: " . $e->getMessage();\n}