class Base {\n    private $allowedProperties = [];\n\n    // Constructor to set allowed properties dynamically or statically.\n    public function __construct() {\n        // You can define allowed properties here or pass them as an array.\n        $this->allowedProperties = get_object_vars($this);\n    }\n\n    public function __set($name, $value) {\n        if (!array_key_exists($name, $this->allowedProperties)) {\n            trigger_error("Cannot dynamically add members to a class", E_USER_ERROR);\n            return false;\n        }\n        $this->$name = $value;\n    }\n}\n\nclass Foo extends Base {\n    public $bar = 5;\n\n    // If needed, you can set additional properties here.\n}\n\n$A = new Foo();\n$A->bar = 10; // This is okay\n\n// The following will trigger an error\n$A->temp = 10;