function extractTextBetweenParentheses($array) {\n    $results = [];\n    foreach ($array as $item) {\n        if (preg_match('/\((.*?)\)/', $item, $matches)) {\n            $results[] = trim($matches[1]);\n        }\n    }\n    return $results;\n}\n\n// Example usage:\n$inputArray = [\n    "(writer) &",\n    "with (additional dialogue)"\n];\n\n$extractedTexts = extractTextBetweenParentheses($inputArray);\n\nprint_r($extractedTexts);