<?php\n    include "config.inc.php"; // Ensure this includes the PDO connection\n\n    $pdo = getPdoConnection(); // Assuming you return a PDO object from config.inc.php\n\n    if(!isset($pdo)) {\n        die('Database connection not established.');\n    }\n\n    try {\n        $stmt = $pdo->prepare("SELECT p_id, p_title FROM projects WHERE c_id = :id");\n        $stmt->execute([':id' => $_GET['id']]);\n        $projects = $stmt->fetchAll(PDO::FETCH_ASSOC);\n        print json_encode($projects);\n    } catch (\PDOException $e) {\n        die('Query failed: ' . $e->getMessage());\n    }\n?>