<?php\n    include "config.inc.php"; // Ensure this includes the database connection\n\n    // Assuming $db_connection is your MySQL link identifier set in config.inc.php\n    if(!isset($db_connection)) {\n        die('Database connection not established.');\n    }\n\n    $id = mysql_real_escape_string($_GET['id'], $db_connection); // Pass the db connection as a second argument\n\n    $sth = mysql_query(\n        sprintf(\n            "SELECT c_id, p_id, p_title FROM projects WHERE c_id = %s",\n            $id\n        ),\n        $db_connection // Specify the database connection here\n    );\n\n    if($sth === false) {\n        die('Query failed: ' . mysql_error());\n    }\n\n    $projects = array();\n    while ($r = mysql_fetch_assoc($sth)) { // Use mysql_fetch_assoc for associative array\n        $projects[] = array('id' => $r['p_id'], 'name' => $r['p_title']);\n    }\n    \n    print json_encode($projects);\n    exit;\n?>