<?php\n// Assuming $dateFromDB is the date string retrieved from your MySQL database\n$dateFromDB = '2009-04-13';\n\n// Set the default locale to Spanish for Spain (or another Spanish-speaking country)\nsetlocale(LC_TIME, 'es_ES.UTF-8');\n\n// Create a DateTime object from the database date string\n$dateObject = new DateTime($dateFromDB);\n\n// Format the date in Spanish\n$formattedDate = $dateObject->format('d F, Y');\n\n// Output the formatted date\necho $formattedDate; // Outputs: 13 abril, 2009\n?>