// Install via composer: composer require firebase/php-jwt\n\nuse Firebase\JWT\JWT;\n\n$key = "your_secret_key";\n$payload = array(\n    "iss" => "http://example.com",\n    "aud" => "http://sub.example.com, http://other-site.com", // List your domains\n    "iat" => time(),\n    "exp" => time() + (60 * 60), // Token expiration in an hour\n    "data" => array(             // Data related to the user can be passed here\n        "user_id" => $userId,\n        "username" => $username\n    )\n);\n\n$jwt = JWT::encode($payload, $key);