<?php\nheader('Content-Type: application/json');\n$response = [\n    'loggedIn' => $user->loggedIn,\n    'sidePanelHtml' => false\n];\nif ($user->loggedIn) {\n    ob_start();\n    ?>\n<!-- side-panel for logged in users here -->\n    <?php\n    $response['sidePanelHtml'] = ob_get_clean();\n} else {\n    ob_start();\n    ?>\n<!-- login/registration etc -->\n    <?php\n    $response['sidePanelHtml'] = ob_get_clean();\n}\necho json_encode($response);\nexit;\n?>