class Input {\n    public function input($key, $default = '') {\n        // Check if it's a POST request\n        if (isset($_POST[$key])) {\n            return filter_input(INPUT_POST, $key, FILTER_SANITIZE_STRING);\n        }\n        \n        // Otherwise, check for GET\n        if (isset($_GET[$key])) {\n            return filter_input(INPUT_GET, $key, FILTER_SANITIZE_STRING);\n        }\n\n        // Return default value if not found\n        return $default;\n    }\n\n    public function get($key, $default = '') {\n        return isset($_GET[$key]) ? filter_input(INPUT_GET, $key, FILTER_SANITIZE_STRING) : $default;\n    }\n\n    public function post($key, $default = '') {\n        return isset($_POST[$key]) ? filter_input(INPUT_POST, $key, FILTER_SANITIZE_STRING) : $default;\n    }\n\n    public function cookies($key, $default = '') {\n        return isset($_COOKIE[$key]) ? filter_input(INPUT_COOKIE, $key, FILTER_SANITIZE_STRING) : $default;\n    }\n}