function generateUuidV4() {\n    if (function_exists('com_create_guid')) {\n        return trim(com_create_guid(), '{}');\n    } elseif (function_exists('random_bytes')) {\n        $bytes = random_bytes(16);\n    } else {\n        $bytes = openssl_random_pseudo_bytes(16);\n    }\n\n    $bytes[6] = chr(ord($bytes[6]) & 0x0f | 0x40); // set variant to 01\n    $bytes[8] = chr(ord($bytes[8]) & 0x3f | 0x80); // set version to 0100\n\n    return vsprintf('%s%s-%s-%s-%s-%s%s', str_split(bin2hex($bytes), 4));\n}\n\necho generateUuidV4();