<?php\n   $pdfFilename = 'document.pdf';\n   $imageOutputPath = 'output.jpg';\n   $pageNumber = 1; // Start from page 1\n\n   try {\n       exec("gs -dNOPAUSE -dBATCH -sDEVICE=jpeg -dFirstPage=$pageNumber -dLastPage=$pageNumber -r300 -sOutputFile=$imageOutputPath \"$pdfFilename\"");\n       echo "Conversion successful. Image saved as $imageOutputPath";\n   } catch (Exception $e) {\n       echo 'Error: ' . $e->getMessage();\n   }\n   ?>