<?php\n\nclass TestClass\n{\n    public $declaredVar;\n    private $undeclaredVar; // If you want it accessible only inside this class using methods\n\n    function __construct()\n    {\n        $this->declaredVar = "I am a declared variable.";\n        $this->undeclaredVar = "I was declared, and now I work properly.";\n    }\n\n    public function display()\n    {\n        echo $this->declaredVar . "<br />";\n        echo $this->getUndeclaredVar();\n        echo "<br /><br />"; \n    }\n\n    private function getUndeclaredVar() {\n        return $this->undeclaredVar;\n    }\n}\n\n$test = new TestClass;\n$test->display();\n\n$test->declaredVar = "The declared variable was changed.";\n// $test->undeclaredVar = "Cannot change directly if it's private";\n\n$test->display();\n\n?>