function getFriendlyDate($date) {\n    $now = new DateTime();\n    $then = new DateTime($date);\n    $interval = $now->diff($then);\n\n    if ($then > $now) {\n        return 'not yet';\n    }\n\n    if ($interval->y >= 1) {\n        return $interval->format('%r%y years ago');\n    } elseif ($interval->m >= 1) {\n        return $interval->format('%r%m months ago');\n    } elseif ($interval->d >= 7) {\n        return intval($interval->d / 7).' weeks ago';\n    } elseif ($interval->d >= 1) {\n        return $interval->format('%r%d days ago');\n    } elseif ($interval->h >= 1) {\n        return $interval->format('%rh hours ago');\n    } elseif ($interval->i >= 1) {\n        return $interval->format('%ri minutes ago');\n    } else {\n        return 'just now';\n    }\n}\n\n// Example usage:\necho getFriendlyDate('2023-10-01 12:00:00'); // Outputs something like "2 weeks ago"