include("class.phpmailer.php");\n\n$mail = new PHPMailer();\n$mail->IsSMTP();\n$mail->SMTPAuth   = true;\n$mail->SMTPSecure = "ssl";\n$mail->Host       = "smtp.gmail.com";\n$mail->Port       = 465;\n\n$mail->Username = "hussaintalha@gmail.com"; // Your Gmail address\n$mail->Password = "your-app-password";      // Use an App Password or OAuth2\n\n$mail->AddReplyTo("hussaintalha@gmail.com", "First Last");\n$mail->From       = "hussaintalha@gmail.com";\n$mail->FromName   = "First Last";\n\n$mail->Subject    = "PHPMailer Test Subject via gmail";\n\n$body = file_get_contents(__DIR__ . '/contents.html'); // Ensure the path is correct\n$mail->MsgHTML($body);\n\n$mail->AddAddress("recipient@example.com", "John Doe");\n\n$mail->IsHTML(true); \n\nif(!$mail->Send()) {\n  echo "Mailer Error: " . $mail->ErrorInfo;\n} else {\n  echo "Message sent!";\n}