<?php\n\nfunction getComposedTerms($sentence, $k) {\n    // Split the sentence into words\n    $words = explode(' ', $sentence);\n    $composedTerms = [];\n\n    // Iterate over each word and combine it with subsequent words up to k times\n    for ($i = 0; $i < count($words); $i++) {\n        for ($j = 0; $j <= $k; $j++) {\n            if ($i + $j >= count($words)) break;\n            // Combine the words from index i to i+j\n            $term = implode(' ', array_slice($words, $i, $j+1));\n            if (!empty($term)) {\n                $composedTerms[] = $term;\n            }\n        }\n    }\n\n    return $composedTerms;\n}\n\n// Example usage\n$sentence = 'John Doe moved to New York last year';\n$k = 3;\n$result = getComposedTerms($sentence, $k);\nprint_r($result);\n\n?>