public function find($xpath) {\n    while ($this->xml_reader->read()) {\n        if ($this->xml_reader->nodeType === XMLREADER::ELEMENT && $this->xml_reader->localName == 'book') {\n            $node = $this->xml_reader->expand();\n            $sx = simplexml_import_dom($node);\n            // Perform XPath on the node\n            $result = $sx->xpath($xpath);\n            if (!empty($result)) {\n                foreach ($result as $item) {\n                    $this->results[] = $item;\n                }\n            }\n        }\n    }\n    return $this->results;\n}