class myClass {\n    static function test1() {\n        if(method_exists(__CLASS__, "test1")) {\n            echo "Hi from test1\n";\n        }\n    }\n\n    function test2() {\n        if(method_exists($this, "test2")) {\n            echo "Hi from test2\n";\n        }\n    }\n\n    static function test3() {\n        if(method_exists(__CLASS__, "test3")) {\n            echo "Hi from test3\n";\n        }\n    }\n}\n\n// Echos Hi from test1\nmyClass::test1();\n\n// Echos Hi from test2\n$obj = new myClass;\n$obj->test2();\n\n// Echos Hi from test3\nmyClass::test3();