class MyClass {\n    private $otherObject;\n\n    public function __construct(OtherClass $otherObject) {\n        $this->otherObject = $otherObject;\n    }\n\n    public function myMethod() {\n        $var1 = $this->otherMethod1();\n        $var2 = $this->otherMethod2();\n        $var3 = $this->otherMethod3();\n        $var4 = $this->otherObject->someMethod();\n\n        // Process variables\n        $var5 = $var1 + $var2 + $var3 + $var4;  // Example processing\n\n        return $var5;\n    }\n\n    private function otherMethod1() {\n        // Method implementation\n        return 1;\n    }\n\n    private function otherMethod2() {\n        // Method implementation\n        return 2;\n    }\n\n    private function otherMethod3() {\n        // Method implementation\n        return 3;\n    }\n}