function checkFileType($filePath) {\n    // Define allowed mime types\n    $allowedMimes = [\n        'image/jpeg' => 1,\n        'image/bmp' => 1,\n        'application/zip' => 1,\n        'application/pdf' => 1,\n        'image/gif' => 1,\n        'application/msword' => 1, // .doc\n        'application/vnd.ms-excel' => 1, // .xls\n        'text/csv' => 1,\n        'application/vnd.openxmlformats-officedocument.wordprocessingml.document' => 1, // .docx\n        'application/x-rar-compressed' => 1, // Assuming rar files are allowed\n    ];\n\n    // Get the mime type using file command\n    $result = exec("file --mime-type -b '{$filePath}'", $_);\n    \n    return isset($allowedMimes[$result]) ? true : false;\n}\n\n// Usage example with a uploaded file:\nif (isset($_FILES['userFile'])) {\n    $tempPath = $_FILES['userFile']['tmp_name'];\n    if (checkFileType($tempPath)) {\n        // Move the file to its final location\n        move_uploaded_file($tempPath, '/path/to/destination/' . basename($_FILES['userFile']['name']));\n    } else {\n        unlink($tempPath); // Delete the temporary file\n        echo "Invalid file type.";\n    }\n} else {\n    echo "No file uploaded.";\n}