<?php\n// Assuming you have a database connection $pdo\n\n$current_time = date('Y-m-d H:i:s');\n$stmt = $pdo->prepare("SELECT * FROM email_queue WHERE scheduled_time <= ?");\n$stmt->execute([$current_time]);\n\nwhile ($email = $stmt->fetch()) {\n    $headers = 'From: '.$email['from_name']."\r\n" .\n               'Reply-To: '.$email['reply_to'] . "\r\n" .\n               'X-Mailer: PHP/' . phpversion();\n    \n    mail($email['to_address'], $email['subject'], $email['message'], $headers);\n    \n    // Once sent, delete from queue\n    $deleteStmt = $pdo->prepare("DELETE FROM email_queue WHERE id = ?");\n    $deleteStmt->execute([$email['id']]);\n}\n?>