use PHPUnit\Framework\TestCase;\n\nclass MyClassTest extends TestCase {\n    public function testMyMethod() {\n        // Create a mock object of OtherClass\n        $mock = $this->createMock(OtherClass::class);\n\n        // Configure the mock to return the expected values when someMethod is called\n        $mock->method('someMethod')\n             ->willReturn(10);\n\n        // Instantiate MyClass with the mocked object\n        $myClass = new MyClass($mock);\n\n        // Call the method and assert the result\n        $result = $myClass->myMethod();\n        $this->assertEquals(16, $result);  // Since other methods return 1, 2, and 3 respectively.\n    }\n}