<?php\n$postData = file_get_contents('php://input');\n$newfile = 'image'; // or some dynamically set filename\n\n// Define the path to save files securely\n$uploadPath = '/path/to/your/directory/' . $newfile . '.jpg';\n\n// Create a new directory resource for handling the image file\n$fileHandle = fopen($uploadPath, 'w');\nif ($fileHandle) {\n    fwrite($fileHandle, $postData);\n    fclose($fileHandle);\n    print $newfile.'.jpg';\n} else {\n    print "Failed to open file.";\n}\n?>