<?php\ndefined('BASEPATH') OR exit('No direct script access allowed');\n\nclass Feed extends CI_Controller {\n\n    public function rss() {\n        // Load your model or use any data source to get the articles\n        $this->load->model('article_model'); // Assuming you have an article model\n        $articles = $this->article_model->get_recent_articles(); // Fetch recent articles\n\n        // Set header content type as XML\n        header("Content-Type: application/rss+xml");\n\n        echo '<?xml version="1.0" encoding="UTF-8"?>';\n        echo '<rss version="2.0">';\n        echo '<channel>';\n        echo '<title>My Website RSS Feed</title>';\n        echo '<link>http://www.example.com</link>';\n        echo '<description>This is an example of RSS 2 feed.</description>';\n\n        // Loop through articles and create item tags\n        foreach ($articles as $article) {\n            echo '<item>';\n            echo '<title>' . htmlspecialchars($article->title, ENT_QUOTES, 'UTF-8') . '</title>';\n            echo '<link>http://www.example.com/article/' . $article->slug . '</link>';\n            echo '<description><![CDATA[' . $article->summary . ']]></description>';\n            echo '<pubDate>' . date('r', strtotime($article->date_published)) . '</pubDate>';\n            echo '</item>';\n        }\n\n        echo '</channel>';\n        echo '</rss>';\n    }\n}\n?>