function getLastLine($file) {\n    $handle = fopen($file, "r");\n    if (!$handle) return false;\n\n    // Move internal pointer to the end of the file\n    fseek($handle, -1, SEEK_END);\n\n    while ($c !== "\n") {\n        // Check for windows new lines (\r\n)\n        if (fgetc($handle) === "\n") break;\n        // Move back one character (step backwards over a newline char)\n        fseek($handle, -2 , SEEK_CUR);\n    }\n\n    // Read line\n    $lastLine = fgets($handle);\n\n    fclose($handle);\n    return trim($lastLine);\n}\n\n$file = 'path/to/your/logfile.log';\necho getLastLine($file);