class JParameter {\n    function render() {\n        // return HTML with tables\n        echo "HTML with tables";\n    }\n}\n\nclass MyParameter {\n    private $jParameter;\n\n    public function __construct(JParameter $jParameter) {\n        $this->jParameter = $jParameter;\n    }\n\n    public function render() {\n        // You can modify the output if needed or just call the original method\n        echo "Modified HTML";\n        // Optionally call the original render and modify its output:\n        // return str_replace("tables", "divs", $this->jParameter->render());\n    }\n}\n\n// existing object\n$jParam = new JParameter();\n$jParam->render();  // Outputs: HTML with tables\n\n// wrap it in your custom class\n$myParam = new MyParameter($jParam);\n$myParam->render();  // Outputs: Modified HTML