<?php\n// Database connection setup\n$servername = "localhost";\n$username = "username";\n$password = "password";\n$dbname = "database_name";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get today's date\n$today = date('Y-m-d');\n\n// Query for current week data (considering Monday as the first day of the week)\n$currentWeekStart = date('Y-m-d', strtotime('this monday'));\n$currentWeekEnd = date('Y-m-d', strtotime('next sunday'));\n\n$result = $conn->query("SELECT * FROM your_table WHERE DATE(your_date_column) BETWEEN '$currentWeekStart' AND '$currentWeekEnd'");\n\n// Fetching data for the current week\nif ($result && $result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "Current Week Data: " . $row['your_column'] . "<br>";\n    }\n}\n\n// Previous week calculation\n$prevWeekStart = date('Y-m-d', strtotime('last monday -1 week'));\n$prevWeekEnd = date('Y-m-d', strtotime('sunday last week'));\n\n$result = $conn->query("SELECT * FROM your_table WHERE DATE(your_date_column) BETWEEN '$prevWeekStart' AND '$prevWeekEnd'");\n\n// Fetching data for the previous week\nif ($result && $result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "Previous Week Data: " . $row['your_column'] . "<br>";\n    }\n}\n\n// Next week calculation\n$nextWeekStart = date('Y-m-d', strtotime('monday next week'));\n$nextWeekEnd = date('Y-m-d', strtotime('sunday next week'));\n\n$result = $conn->query("SELECT * FROM your_table WHERE DATE(your_date_column) BETWEEN '$nextWeekStart' AND '$nextWeekEnd'");\n\n// Fetching data for the next week\nif ($result && $result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "Next Week Data: " . $row['your_column'] . "<br>";\n    }\n}\n\n$conn->close();\n?>