$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$max = 5;\n\n// Calculate pagination limit\n$limit = ($page - 1) * $max;\n\ntry {\n    // First query to get the oldest post\n    $oldestPostQuery = "SELECT * FROM posts ORDER BY date ASC LIMIT 1";\n    $stmtOldest = $pdo->query($oldestPostQuery);\n    $oldestPost = $stmtOldest->fetchAll(PDO::FETCH_ASSOC);\n\n    // Second query to get the rest of the posts in descending order, excluding the oldest post\n    $restPostsQuery = "SELECT * FROM posts WHERE id <> (SELECT id FROM posts ORDER BY date ASC LIMIT 1) ORDER BY date DESC LIMIT :limit OFFSET :offset";\n    $stmtRest = $pdo->prepare($restPostsQuery);\n    $stmtRest->bindParam(':limit', $max, PDO::PARAM_INT);\n    $stmtRest->bindParam(':offset', $limit, PDO::PARAM_INT);\n    $stmtRest->execute();\n    $restPosts = $stmtRest->fetchAll(PDO::FETCH_ASSOC);\n\n    // Combine both results\n    $result = array_merge($oldestPost, $restPosts);\n\n    // Prepare to calculate total pages\n    $totalresStmt = $pdo->query("SELECT COUNT(id) AS tot FROM posts");\n    $totalres = $totalresStmt->fetchColumn();\n    $totalpages = ceil($totalres / $max);\n} catch (PDOException $e) {\n    echo 'Connection failed: ' . $e->getMessage();\n}