<?php\n// Example YouTube URL\n$youtubeUrl = 'https://www.youtube.com/watch?v=VIDEO_ID';\n\n// Extract the video ID using a regular expression\npreg_match('/(?<=v=)[\w-]+(?=&|$)|[\w-]+$/', $youtubeUrl, $matches);\n$videoId = $matches[0];\n\n// Embed code template\n$embedCodeTemplate = '<iframe width="560" height="315" src="https://www.youtube.com/embed/%s" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe>';\n\n// Generate the embed code with the extracted video ID\n$embedCode = sprintf($embedCodeTemplate, $videoId);\n\n// Output the embed code\necho $embedCode;\n?>