// Query for fetching the paginated images\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$limit = 16;\n$offset = ($page - 1) * $limit;\n\n$sql_data = "SELECT p.*, im.* \n             FROM posts p \n             INNER JOIN postimages pi ON pi.post_id = p.id\n             INNER JOIN images im ON pi.image_id = im.image_id \n             WHERE i.active = 1 \n             ORDER BY p.created_at \n             LIMIT $limit OFFSET $offset";\n\n// Query for getting the total count of images\n$sql_count = "SELECT COUNT(*) as total \n              FROM posts p \n              INNER JOIN postimages pi ON pi.post_id = p.id\n              INNER JOIN images im ON pi.image_id = im.image_id \n              WHERE i.active = 1";\n\n// Execute both queries and fetch results\n$result_data = mysqli_query($conn, $sql_data);\n$total_count_result = mysqli_query($conn, $sql_count);\n$total_row = mysqli_fetch_assoc($total_count_result);\n$total_images = $total_row['total'];\n\n// Calculate total pages\n$total_pages = ceil($total_images / $limit);\n\n// Display your images and pagination links here using $result_data and $total_pages