<?php\n// Get parameters from AJAX request\n$column = isset($_GET['column']) ? $_GET['column'] : 'default_column'; // Set a default column if necessary\n$order = isset($_GET['order']) && strtoupper($_GET['order']) === 'ASC' ? 'ASC' : 'DESC';\n\n// Connect to MySQL (using PDO for better security)\n$pdo = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n\n// Prepare and execute the query\n$stmt = $pdo->prepare("SELECT * FROM your_table ORDER BY :column :order");\n$stmt->execute(['column' => $column, 'order' => $order]);\n\n$output = '';\nwhile ($row = $stmt->fetch()) {\n    $output .= '<tbody>';\n    $output .= '<tr><td>â€¢</td><td>' . htmlspecialchars($row["col1"]) . '</td><td>' . htmlspecialchars($row["col2"]) . '</td><td></td><td></td></tr>';\n    $output .= '<tr><td></td><td colspan="4">' . htmlspecialchars($row["col3"]) . '</td></tr>';\n    $output .= '</tbody>';\n}\n\necho $output;\n?>