<?php\n\nfunction addXmlNode($filePath, $newNodeData) {\n    // Create a new DOMDocument object\n    $dom = new DOMDocument();\n    \n    // Load the existing XML file into DOMDocument\n    if (!$dom->load($filePath)) {\n        die("Failed to load the XML document.");\n    }\n    \n    // Define the root node - adjust as necessary\n    $rootNode = $dom->documentElement;\n    \n    // Import new node from string\n    $newDoc = new DOMDocument();\n    $newDoc->loadXML('<wrapper>' . trim($newNodeData) . '</wrapper>');\n    \n    // Import the first child (the actual node you want to add)\n    $importedNode = $dom->importNode($newDoc->documentElement->firstChild, true);\n    \n    // Append the new node to the root\n    $rootNode->appendChild($importedNode);\n    \n    // Save the changes to the file\n    $dom->save($filePath);\n}\n\n// Example usage:\n$dataToAdd = '<entry id="1_1">\n    <title>This is the title 1</title>\n    <description>This is the description...</description>\n    <subName>Publishers Name</subName>\n    <date>Saturday, June 11th, 2007, 5:46:21 PM</date>\n    <BorF>bug</BorF>\n</entry>';\n\naddXmlNode('bugs_features_listdata.xml', $dataToAdd);\n\n?>