<?php\n$apiKey = "YOUR_ALPHA_VANTAGE_API_KEY";\n$symbol = "AAPL"; // Example symbol\n$url = "https://www.alphavantage.co/query?function=TIME_SERIES_INTRADAY&symbol=$symbol&interval=1min&apikey=$apiKey";\n\n$response = file_get_contents($url);\n$data = json_decode($response, true);\n\nif ($data && isset($data['Time Series (1min)'])) {\n    echo "<pre>";\n    print_r(array_slice($data['Time Series (1min)'], 0, 5)); // Show latest 5 entries\n    echo "</pre>";\n} else {\n    echo "Error fetching data.";\n}\n?>