<?php\n\n$dsn = 'mysql:host=localhost;dbname=your_database;charset=utf8mb4';\n$username = 'your_username';\n$password = 'your_password';\n\ntry {\n    $pdo = new PDO($dsn, $username, $password);\n} catch (PDOException $e) {\n    die("Could not connect to the database: " . $e->getMessage());\n}\n\n// Fetch questions and answers in one query\n$query = $pdo->prepare('SELECT * FROM andyasks ORDER BY date_posted ASC');\n$query->execute();\n$results = $query->fetchAll(PDO::FETCH_ASSOC);\n\n$previousQuestion = null;\nforeach ($results as $row) {\n    if ($row['user'] == 'Andy') {\n        // Print the question\n        echo "<p>" . htmlspecialchars($row["questions"]) . "</p>";\n        $previousQuestion = &$row;\n    } else {\n        if ($previousQuestion !== null) {\n            // Print the answer associated with the latest question\n            echo "<p>Answer: " . htmlspecialchars(str_replace('@amcafee', '', $row["questions"])) . "</p>";\n        }\n    }\n}\n\n?>