<?php\n\nfunction insertImageIntoRtf($rtfFilePath, $imagePath, $placeholder) {\n    // Read the existing RTF file content\n    $rtfContent = file_get_contents($rtfFilePath);\n    if ($rtfContent === false) {\n        return "Failed to read RTF file.";\n    }\n\n    // Read and encode the image in base64\n    $imageData = file_get_contents($imagePath);\n    if ($imageData === false) {\n        return "Failed to read image file.";\n    }\n    $base64Image = chunk_split(base64_encode($imageData));\n\n    // Prepare RTF code for embedded image\n    $imageRtf = '{\binhtml';  // Start binary HTML section in RTF\n    $imageRtf .= '<html><body>';\n    $imageRtf .= "<img src='data:image/png;base64,{$base64Image}' />";\n    $imageRtf .= '</body></html>';\n    $imageRtf .= '}\binhtml0';  // End binary HTML section in RTF\n\n    // Replace the placeholder with the image code\n    $updatedRtfContent = str_replace($placeholder, $imageRtf, $rtfContent);\n\n    // Write the updated RTF content back to the file or a new one\n    if (file_put_contents($rtfFilePath, $updatedRtfContent) === false) {\n        return "Failed to write to RTF file.";\n    }\n\n    return "Image was successfully inserted into RTF file.";\n}\n\n// Example usage\n$rtfPath = 'example.rtf';\n$imagePath = 'photo.png';\n$placeholderText = '{photo}';\n\necho insertImageIntoRtf($rtfPath, $imagePath, $placeholderText);