function cleanMicrosoftWordHTML($html) {\n    $config = array(\n        'indent' => true,\n        'output-xhtml' => true,\n        'wrap' => 200,\n        'clean' => true,\n        'show-body-only' => true,\n        'drop-empty-elements' => false,\n        'fix-backslash' => true,\n    );\n\n    $tidy = tidy_parse_string($html, $config, 'utf8');\n    tidy_clean_repair($tidy);\n\n    // Convert cleaned HTML to string\n    $cleanedHtml = (string)$tidy;\n\n    // Remove MS Office specific tags and styles using regex\n    $patterns = array(\n        '/<o:p>[\s\S]*?<\/o:p>/i',  // remove <o:p></o:p>\n        '/class="Mso[^"]*"/i',       // remove all classes starting with Mso\n        '/style=["\'][^"\']*["\']/i'   // remove inline styles\n    );\n\n    $cleanedHtml = preg_replace($patterns, '', $cleanedHtml);\n\n    return $cleanedHtml;\n}\n\n// Usage\n$dirtyHTML = "Your pasted MS Word content here...";\necho cleanMicrosoftWordHTML($dirtyHTML);