function replaceSpecialCharacters($html) {\n    // Replace curly quotes with straight quotes, em dashes, etc.\n    $replacePairs = array(\n        '‘' => "'",  // left single quote to apostrophe\n        '’' => "'",  // right single quote to apostrophe\n        '“' => '"',  // left double quote to quotation mark\n        '”' => '"',  // right double quote to quotation mark\n        '–' => '-',  // en dash to hyphen\n        '—' => '--'  // em dash to double hyphen\n    );\n\n    $html = str_replace(array_keys($replacePairs), array_values($replacePairs), $html);\n\n    return $html;\n}\n\n// Usage with cleaned HTML\n$cleanerHTML = replaceSpecialCharacters(cleanMicrosoftWordHTML($dirtyHTML));\necho $cleanerHTML;