class Browser {\n    private $isStopped = false;\n\n    public function isStatusCode($code) {\n        if ($this->isStopped) return $this;\n        // Perform check\n        return $this;\n    }\n\n    public function click($what) {\n        if ($this->isStopped) return $this;\n        // Click logic\n        return $this;\n    }\n\n    public function stop() {\n        $this->isStopped = true;\n        return $this;\n    }\n\n    public function I_DONT_WANT_THIS_TO_RUN() {\n        if ($this->isStopped) return $this;\n        // Logic that should be skipped\n        return $this;\n    }\n\n    public function thenThisRunsOkay() {\n        // This method will run as it is not conditional on the stop flag.\n    }\n}\n\n$browser = new Browser();\n$browser->isStatusCode(200)\n  ->click('Register')\n  ->stop()\n  ->I_DONT_WANT_THIS_TO_RUN();\n\n$browser->thenThisRunsOkay();