<?php\n$files = glob('images/*.{jpg,png}', GLOB_BRACE);\n\nif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])) {\n    $file = basename($_POST['filename']);\n    if ($_POST['action'] === 'rename') {\n        rename("images/$file", "images/{$_POST['newname']}");\n    } elseif ($_POST['action'] === 'delete') {\n        unlink("images/$file");\n    }\n}\n?>\n<!DOCTYPE html>\n<html lang="en">\n<head>\n    <meta charset="UTF-8">\n    <title>Photo Gallery</title>\n    <script src="https://code.jquery.com/jquery-3.6.0.min.js"></script>\n    <style>\n        .thumbnail { width: 150px; height: auto; margin: 5px; }\n        .caption { text-align: center; }\n    </style>\n</head>\n<body>\n<h2>Photo Gallery Manager</h2>\n<div id="gallery">\n<?php foreach ($files as $file): ?>\n    <div class="photo">\n        <img src="<?=$file?>" alt="" class="thumbnail">\n        <div class="caption">\n            <input type="text" value="<?=basename($file)?>">\n            <button data-action="rename" data-filename="<?=basename($file)?>">Rename</button>\n            <button data-action="delete" data-filename="<?=basename($file)?>">Delete</button>\n        </div>\n    </div>\n<?php endforeach; ?>\n</div>\n\n<script src="manage.js"></script>\n</body>\n</html>