<?php\n\n$url = "http://chacha102.com/feed/";\n$xml = simplexml_load_file($url);\n\n// Register the namespace used for 'content'\n$xml->registerXPathNamespace('content', 'http://purl.org/rss/1.0/modules/content/');\n\nforeach ($xml->channel->item as $item) {\n    // Try to get content:encoded first\n    $fullContent = $item->xpath('content:encoded');\n    if (!empty($fullContent)) {\n        $fullContentText = (string)$fullContent[0];\n    } else {\n        // Fallback to description if content is not available\n        $fullContentText = (string)$item->description;\n    }\n    \n    echo "Title: " . htmlspecialchars($item->title) . "\n";\n    echo "Full Content:\n" . htmlspecialchars(strip_tags($fullContentText)) . "\n\n";\n}\n\n?>