<?php\n// add_case.php\n\n// Basic input validation and sanitization\n$details = htmlspecialchars(trim($_POST['details'] ?? ''));\n\nif(empty($details)) {\n    http_response_code(400);\n    echo 'Invalid case details.';\n    exit;\n}\n\ntry {\n    // Database connection setup - replace with your actual database details\n    $conn = new PDO('mysql:host=localhost;dbname=your_database', 'username', 'password');\n    $conn->setAttribute(PDO::ATTR_ERRMODE, PDO::ERRMODE_EXCEPTION);\n\n    // Insert data into the database\n    $stmt = $conn->prepare("INSERT INTO cases (details) VALUES (:details)");\n    $stmt->execute([':details' => $details]);\n\n} catch(PDOException $e) {\n    echo "Error: " . $e->getMessage();\n    http_response_code(500);\n    exit;\n}\n\necho 'Case added successfully.';\n?>