<?php\n// Example: Checking if WordPress Core is installed\nif (!file_exists('wp-config.php')) {\n    echo "WordPress not installed. Please run the installation script.";\n} else {\n    require_once('wp-load.php'); // Load WordPress Environment\n\n    // Create a new post as an example of extending WordPress functionality\n    $new_post = array(\n        'post_title' => 'Sample Post',\n        'post_content' => 'This is an example of creating a new post programmatically.',\n        'post_status' => 'publish'\n    );\n\n    wp_insert_post($new_post);\n}\n?>