// Database connection using PDO with UTF-8\n$dsn = "mysql:host=localhost;dbname=your_database;charset=utf8mb4";\n$options = [\n    PDO::ATTR_ERRMODE            => PDO::ERRMODE_EXCEPTION,\n    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,\n    PDO::ATTR_EMULATE_PREPARES   => false,\n];\ntry {\n    $pdo = new PDO($dsn, 'username', 'password', $options);\n} catch (\PDOException $e) {\n    throw new \PDOException($e->getMessage(), (int)$e->getCode());\n}\n\n// Assuming you have user input stored in $userInput\nrequire_once '/path/to/HTMLPurifier.auto.php';\n$config = HTMLPurifier_Config::createDefault();\n$config->set('Core.Encoding', 'UTF-8');\n$purifier = new HTMLPurifier($config);\n\n$cleanHtml = $purifier->purify($userInput);\n\n// Save $cleanHtml to your database\n$stmt = $pdo->prepare("INSERT INTO your_table (content) VALUES (:content)");\n$stmt->execute([':content' => $cleanHtml]);\n\n// When outputting the content\necho mb_convert_encoding($cleanHtml, 'HTML-ENTITIES', 'UTF-8');