function patch_string($orig, $patch) {\n    $lines = explode("\n", $patch);\n    $origLines = array_merge(explode(' ', $orig), [null]); // Add null to handle end-of-string\n\n    $i = 0;\n    $output = [];\n    \n    foreach ($lines as $line) {\n        switch (substr($line, 0, 1)) {\n            case '+': // Addition\n                array_splice($output, $i, 0, substr($line, 1));\n                break;\n            case '-': // Deletion\n                unset($origLines[$i]);\n                break;\n            default: // No change or diff header info\n                if (isset($lines[1]) && substr($lines[1], 0, 1) == '+') {\n                    array_splice($output, $i, 0, substr($line, 1));\n                } else {\n                    $i++;\n                }\n        }\n    }\n\n    return implode(' ', array_values(array_merge($origLines, $output)));\n}\n\n// Example usage:\n$newText = patch_string($doc1, $unifiedDiff);\necho $newText;